import React from "react";
import movieFinderPreview  from '../assets/Alabaster-Alligator_sc.png'
import '../styles/projects.css'

const projects = [
    {
      name: "Movie Finder",
      link: "https://eschase4.github.io/Alabaster-Alligators_sc.png/",
      preview: movieFinderPreview,
      description: "My first bootcamp project. It allows users to take a quiz about the mood that they are in, and recommends a movie based on the results"
    }
  ]

function Projects(props) {

    return (
        <div class="projectContainer">
            <div class="titleCard">
                <h3>Projects</h3>
            </div>
            <div class="projectList">
                <ul>
                    
                    <li><a href={projects.link}><img src={projects.preview} alt={projects.descriptionescription}></img></a></li>
                </ul>
            </div>
        </div>
    )
}

export default Projects
